# import re
import regex as re
import sys
filename = sys.argv[1]

left_aligned = ['105', '118', '126', '131', '135', '136', '156', '158', '163', '184', '31', '35', '49', '52', '53', '58', '60', '76', '85', '93', '95', '97']

with open(filename, 'r') as f:
    for idx, line in enumerate(f.readlines()):
        cleaned = re.sub(' [\u0591-\u05C7]+ ', '', line)
        cleaned = re.sub('[\u0591-\u05C7]+ ', '', cleaned)
        cleaned = re.sub(' [\u0591-\u05C7]+', '', cleaned)

        cleaned = re.sub(' &#14[3-6][0-9]; ', '', cleaned)
        cleaned = re.sub('&#14[3-6][0-9]; ', '', cleaned)
        cleaned = re.sub(' &#14[3-6][0-9];', '', cleaned)
        
        p_idx = cleaned.find('<p class=') 
        if p_idx != -1:
            p_idx += 11 #end class string
            is_valid = True
            
            #skip any left align text p_idx
            class_num = cleaned[p_idx: cleaned.find('"', p_idx, len(cleaned))]
            for num in left_aligned:
                if class_num == num:
                    is_valid = False
            if is_valid:
                cleaned = cleaned[:-2]
                cleaned += ' style="direction: rtl;">'
        
        # reverse brackates
        num_brackets = re.search('(?<=>).+(\)).+(\()', cleaned)
        if num_brackets:
            cleaned = re.sub('\(', '((', cleaned)
            cleaned = re.sub('\)', '(', cleaned)
            cleaned = re.sub('\(\(', ')', cleaned)
            # <span class="c21">)תהלים ע"ח, ו'(</span> do not work
        
        # , after number
        num_comma = re.search('(?<![0-9]+)(?<=>.+),[0-9]+', cleaned)
        if num_comma:
            a = num_comma.group()
            a = a.replace(',', '')
            cleaned = cleaned.replace(num_comma.group(), a + ',')
            # print(line, num_comma.group(), cleaned)

            
                
        # p of sub c22(apac) spact top
        # spak with image horisontal display: grid;
        # ,/. after number
        


        #        cleaned = re.sub('text-align:left', 'text-align:right', cleaned)

        
        print(cleaned)


